import React from "react";

const Dashboard = () => {

  const shareLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        alert(`Latitude: ${position.coords.latitude}, Longitude: ${position.coords.longitude}`);
      });
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  };

  const handleLogout = () => {
    // Redirect to Login page on logout
    history.push("/login");
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-400 to-purple-600">
      {/* Navbar */}
      <nav className="bg-white shadow-md py-4">
        <div className="max-w-7xl mx-auto px-10 flex justify-between items-center">
          <h1 className="text-2xl md:text-3xl font-bold text-blue-600">Live Tracking</h1>
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 transition duration-300"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Dashboard Content */}
      <div className="w-full max-w-md md:max-w-lg mx-auto bg-white p-8 mt-10 rounded-lg shadow-xl">
        <h2 className="text-3xl md:text-4xl font-bold text-center text-gray-800 mb-6">Welcome to Your Dashboard</h2>
        <p className="text-lg md:text-xl text-center text-gray-600 mb-8">
          Track your real-time location and share it with others.
        </p>

        <button
          onClick={shareLocation}
          className="bg-purple-500 text-white py-3 px-6 rounded-full hover:bg-purple-600 transition duration-300 w-full md:w-auto mx-auto"
        >
          Share My Location
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
