import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);

  // Function to get current location
  const getLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation(position.coords);
        },
        (err) => {
          setError("Unable to retrieve location. Please try again.");
        }
      );
    } else {
      setError("Geolocation is not supported by this browser.");
    }
  };

  useEffect(() => {
    // Optionally get the location on initial load
    getLocation();
  }, []);

  return (
    <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
      <div className="max-w-4xl w-full p-8 rounded-lg shadow-lg bg-gradient-to-r from-blue-500 to-green-500">
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold animate__animated animate__fadeIn">
            Live Tracking Dashboard
          </h1>
          <p className="text-xl mt-2">Track your location in real-time</p>
        </div>

        <div className="text-center">
          {location ? (
            <div className="animate__animated animate__fadeInUp">
              <p className="text-lg">Latitude: {location.latitude}</p>
              <p className="text-lg">Longitude: {location.longitude}</p>
            </div>
          ) : error ? (
            <p className="text-lg text-red-500">{error}</p>
          ) : (
            <p className="text-lg">Fetching your location...</p>
          )}
        </div>

        <div className="text-center mt-6">
          <button
            onClick={getLocation}
            className="px-6 py-3 bg-blue-700 hover:bg-blue-800 rounded-lg text-white font-bold transform transition-all duration-300 ease-in-out hover:scale-105"
          >
            Share My Location
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
