import React from "react";
// import { useHistory } from "react-router-dom";

const Dashboard = () => {
  const history = useHistory();

  const shareLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        alert(`Latitude: ${position.coords.latitude}, Longitude: ${position.coords.longitude}`);
      });
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  };

  const handleLogout = () => {
    // Redirect to Login page on logout
    history.push("/login");
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-400 to-purple-600">
      {/* Navbar */}
      <nav className="bg-white shadow-md py-4">
        <div className="max-w-6xl mx-auto px-6 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-blue-600">Live Tracking</h1>
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white py-2 px-4 rounded hover:bg-red-600 transition duration-300"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Dashboard Content */}
      <div className="w-full max-w-md mx-auto bg-white p-8 mt-10 rounded-lg shadow-xl">
        <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">Welcome to Your Dashboard</h2>
        <p className="text-lg text-center text-gray-600 mb-8">
          Track your real-time location and share it with others.
        </p>

        <button
          onClick={shareLocation}
          className="bg-purple-500 text-white py-3 px-6 rounded-full hover:bg-purple-600 transition duration-300 w-full"
        >
          Share My Location
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
